import react, { Component } from "react";
import Field from "./Field"
import PostedField from "./PostedField";

class Section extends Component {
    constructor(props){
        super(props)

        this.state = {
            posted: false,
        };

        this.generateFields = this.generateFields.bind(this);
        this.makePostComps = this.makePostComps.bind(this);
        this.getVals = this.getVals.bind(this);
    }


    generateFields(){
        const fields = this.props.fields;

        const fieldComps = fields.map(field=>{
            return (
                <Field name={field.name} labelText={field.labelText} type={field.type}></Field>
            )
        })

        return fieldComps;
    }

    makePostComps(vals){
        const fields = this.props.fields;

        const postComps = fields.map((field, i)=>{
           return <PostedField labelText={field.labelText}>{vals[i]}</PostedField>
        })

        this.setState({
            posted: true,
        })

        return postComps;
    }

    getVals(){
        const valNodes = document.querySelectorAll(`#${this.props.sectionID} > .input-wrap > input`)
        const vals = Array.from(valNodes).map(node=>node.val);

        console.log(vals);
    }


    render(){
        const fields = this.generateFields();

        return (
            <div className="section" id={this.props.sectionID}>
                <p className="form-title">{this.props.sectionName}</p>
                <form>
                    {fields}
                    <button type="submit" onSubmit={this.getVals()}>Submit Section</button>
                </form>

            </div>
        )
    }
}

export default Section;
